// 新規作成 変数number
let number;
// 変数number 入れろ 数値1 + 数値1
number = 1 + 1;
// 変数number 入れろ 数値1 - 数値1
number = 1 - 1;
// 変数number 入れろ 数値1 × 数値1
number = 1 * 1;
// 変数number 入れろ 数値1 ÷ 数値1
number = 1 / 1;
// 変数number 入れろ 数値1 余り 数値1
number = 1 % 1;
// 変数number 入れろ 数値1 - ( 数値1 × 数値1 ) ÷ 数値1
number = 1 - (1 * 1) / 1;
